function headerBackChange(){const e=document.getElementById("head"),t=document.getElementById("guide-list");t.addEventListener("scroll",()=>{0<t.scrollTop?e.classList.add("back-white"):e.classList.remove("back-white")})}function asideToggleActive(){document.getElementById("guide-list").classList.toggle("open-aside")}function select(){document.querySelectorAll(".field.select").forEach(e=>{var t=e.querySelector(".selected");const c=e.querySelector(".option-area");t.addEventListener("click",function(){c.classList.contains("active")?c.classList.remove("active"):c.classList.add("active")})})}